import sys


def d(p1, p2):
    return (p1[0] - p2[0]) ** 2 + (p1[1] - p2[1]) ** 2


def heronx16(p1, p2, p3):
    a = d(p1, p2)
    b = d(p1, p3)
    c = d(p2, p3)
    return (a + b + c) ** 2 - 2 * (a ** 2 + b ** 2 + c ** 2)


