import itertools


def count_inversions(enumerate_seq):
    tmp = list(enumerate_seq[:])
    result = 0
    for i in range(len(tmp)):
        for j in range(len(tmp) - 1):
            if tmp[j][0] > tmp[j + 1][0]:
                result += 1
                tmp[j], tmp[j + 1] = tmp[j + 1], tmp[j]
    return result


def sub_seq(a, b):
    i, j = 0, 0
    while i < len(a) and j < len(b):
        if a[i] == b[j]:
            i += 1
        j += 1
    return i == len(a)


