def process(S, k):
    n = len(S)
    L = [x for x in S]
    needed = set([i for i in range(k)])
    question = []
    for i in range(n):
        if S[i] != '?' and S[n-1-i] != '?' and S[i] != S[n-1-i]:
            return 'IMPOSSIBLE'
        if i <= n-1-i and S[i] != '?':
            L[n-1-i] = S[i]
            c = ord(S[i])-ord('a')
            if c in needed:
                needed.remove(c)
            if c >= k:
                return 'IMPOSSIBLE'
        if i <= n-1-i and S[n-1-i] != '?':
            L[i] = S[n-1-i]
            c = ord(S[n-1-i])-ord('a')
            if c in needed:
                needed.remove(c)
            if c >= k:
                return 'IMPOSSIBLE'
        if i <= n-1-i and S[i]==S[n-1-i]=='?':
            question.append(i)
    if len(needed) > len(question):
        return 'IMPOSSIBLE'
    needed = list(needed)
    needed = sorted(needed)
    while len(needed) > 0:
        c = needed.pop()
        c = chr(c+ord('a'))
        i = question.pop()
        L[i] = c
        L[n-1-i] = c
    for i in question:
        L[i] = 'a'
        L[n-1-i] = 'a'
    return ''.join(L)

