def search(l,r,s,g):
    if l>r:
        return [-1,g]
    mid=(l+r)//2
    sum=0
    temp=mid
    while temp>0:
        sum+=temp%10
        temp//=10
    if mid-sum==s:
        return [mid-mid%10,g]
    elif mid-sum>s:
        g=mid-mid%10
        return search(l,mid-1,s,g)
    else:
        return search(mid+1,r,s,g)
