def dijkstra(n,graph):
    for i in range(n):
        graph[i][i] = -1    #removing all the self loops
        
    path = defaultdict(int)    
    infinite = float('inf')    
    visited = [True]*n
    dist = [infinite]*(n-1)
    dist = [0] + dist
        
    if graph[n-1][0] == 1:  #checking that is train is connected to Town n
        while True:
            min_dist = min(dist)
            ind = dist.index(min_dist)
                                                  #checking for path of bus
            if min_dist != infinite:
                if visited[ind]:
                    visited[ind] = False
                    for i in range(n):
                        if visited[i]:
                            if graph[ind][i] == 0:
                                mi = min_dist + 1
                                if mi<dist[i]:
                                    dist[i] = mi
                                    path[i] = mi
                dist[ind] = infinite
            else:
                break
        if visited[n-1]:
            return -1
        return path[n-1]   
    else:
        while True:
            min_dist = min(dist)
            ind = dist.index(min_dist)    #checking for path of train
            
            if min_dist != infinite:
                if visited[ind]:
                    visited[ind] = False
                    for i in range(n):
                        if visited[i]:
                            if graph[ind][i] != 0:
                                mi = min_dist + 1
                                if mi<dist[i]:
                                    dist[i] = mi
                                    path[i] = mi
                dist[ind] = infinite
            else:
                break
        if visited[n-1]:
            return -1
        return path[n-1]
        
    
