def isPrime(x: int) -> bool:
    if x < 3:
        return x == 2
    if x % 2 != 1:
        return False
    A = [2, 325, 9375, 28178, 450775, 9780504, 1795265022]
    d = x - 1
    r = 0
    while d % 2 == 0:
        r += 1
        d //= 2
    for a in A:
        v = pow(a, d, x)
        if v <= 1 or v == x - 1:
            continue
        for i in range(r):
            v = v * v % x
            if v == x - 1 and i != r - 1:
                v = 1
                break
            # 在中途而非开头得到1，却没有经过 - 1，说明存在其他数字y≠-1满足y ^ 2≡1，则x一定不是奇素数
            if v == 1:
                return False
        if v != 1:
            return False
    return True


