def check(x, y, direction):
    ans = 1
    curr_x = x + direction[0]
    curr_y = y + direction[1]
    while valid(curr_x, curr_y) and a[curr_x][curr_y] == 'X':
        ans += 1
        curr_x += direction[0]
        curr_y += direction[1]
    curr_x = x - direction[0]
    curr_y = y - direction[1]
