class Car:
  def __init__(self, id, size, location):
    self.id = id
    self.size = size
    self.location = location
    print(location)

cars = []
lot_size, back_gap, front_gap = map(int, input().split())
q = int(input())
for id in range(1, q + 1):
  request, value = map(int, input().split())
  if request == 1:
    size = value
    # Only car in the lot.
    if len(cars) == 0:
      if size <= lot_size:
        cars.append(Car(id, size, 0))
      else:
        print(-1)
      continue
    # Park before first car.
    if size + front_gap <= cars[0].location:
      cars.insert(0, Car(id, size, 0))
      continue
    # Park between two cars.
    found = False
    for pos in range(1, len(cars)):
      a, b = cars[pos - 1], cars[pos]
      x = a.location + a.size + back_gap
      if x + size + front_gap <= b.location:
        cars.insert(pos, Car(id, size, x))
        found = True
        break
    if found:
      continue
    # Park after last car.
    a = cars[len(cars) - 1]
    x = a.location + a.size + back_gap
    if x + size <= lot_size:
      cars.append(Car(id, size, x))
      continue
    # Fail.
    print(-1)
  else:
    id = value
    for pos, car in enumerate(cars):
      if car.id == id:
        del cars[pos]
        break

from typing import List

def prettiest_permutation(n :int, arr: List[int], s: List[str]) -> List[int]:
    par=[0]*310
    rank=[0]*310
    def init_union_find(V):#initial state
        for i in range(V):
            par[i]=i
            rank[i]=0
    def find(x):#where the root
        if par[x]==x:
            return x
        else:
            par[x]=find(par[x])
            return par[x]
    def unite(x,y):#x and y unite
        x=find(x)
        y=find(y)
        if x==y:
            return
        if rank[x]<rank[y]:
            par[x]=y
        else:
            par[y]=x
            if(rank[x] == rank[y]): rank[x] += 1

    init_union_find(310)
    edge=[0]*310
    node=[0]*310

    uv=[]
    for i in range(n):
        for j in range(n):
            if s[i][j]=='1':
                uv.append([i,j])
    for u,v in uv:
        unite(u,v)

    for i in range(len(arr)):
        for j in range(i+1,len(arr)):
            if arr[i]>arr[j] and find(i)==find(j):
                arr[i], arr[j]=arr[j], arr[i]
    return arr

