def solve(n, m, ABs):
    edges = [(a, -b, i) for i, (a, b) in enumerate(ABs)]
    edges.sort()
    ans = [(0, 0) for i in range(m)]
    v = 0
    count = 0
    s = 1
    t = 2
    for a, mb, i in edges:
        count += 1
        if mb == -1:
            v += 1
            ans[i] = (0, v)
        else:
            if t > v:
                return False, []
            ans[i] = (s, t)
            if t == s + 1:
                s = 1
                t += 1
            else:
                s += 1
    return True, ans

