def inside(n, m, x, y):
    return 0 <= x <= n and 0 <= y <= m

def corner(n, m, x, y):
    return x in [0, n] and y in [0, m]

def next(n, m, x, y, t, a, b):
    for cx in [0, n]:
        cy = a * cx + b
        if inside(n, m, cx, cy) and (cx, cy) != (x, y):
            nx, ny = cx, cy
    for cy in [0, m]:
        cx = (cy - b) // a
        if inside(n, m, cx, cy) and (cx, cy) != (x, y):
            nx, ny = cx, cy
    nt = t + abs(nx - x)
    na = -a
    nb = ny - na * nx
    if corner(n, m, nx, ny):
        return None
    return nx, ny, nt, na, nb


