class Rectangle:
    def __init__(self, a, b, letter):
        self.a = a
        self.b = b
        self.letter = letter

    def t(self):
        return Rectangle(self.b, self.a, self.letter)

    def __eq__(self, other):
        return self.letter == other.letter


class Square1:
    def __init__(self, r1, r2, r3):
        self.r1 = r1
        self.r2 = r2
        self.r3 = r3

    def is_square(self):
        return self.r1.a == self.r2.a and self.r1.a == self.r3.a and self.r1.a == (self.r1.b + self.r2.b + self.r3.b)

    def draw(self):
        print(self.r1.a)
        r1 = (self.r1.letter * self.r1.a + '\n') * self.r1.b
        r2 = (self.r2.letter * self.r2.a + '\n') * self.r2.b
        r3 = (self.r3.letter * self.r3.a + '\n') * self.r3.b
        print(r1 + r2 + r3, end='')


class Square2:
    def __init__(self, r1, r2, r3):
        self.r1 = r1
        self.r2 = r2
        self.r3 = r3

    def is_square(self):
        return self.r1.a == (self.r2.a + self.r3.a) and self.r2.b == self.r3.b and self.r1.a == (self.r1.b + self.r2.b)

    def draw(self):
        print(self.r1.a)
        r1 = (self.r1.letter * self.r1.a + '\n') * self.r1.b
        bottom = (self.r2.letter * self.r2.a + self.r3.letter * self.r3.a + '\n') * self.r2.b
        print(r1 + bottom, end='')


