def build_sparse_table_plus(array):
    n = len(array)
    log_n = int(math.log2(n)) + 1
    sparse_table = [[0] * n for _ in range(log_n)]
 
    for i in range(n):
        sparse_table[0][i] = array[i]
 
    for i in range(1, log_n):
        for j in range(n - (1 << i) + 1):
            sparse_table[i][j] = max(
                sparse_table[i - 1][j],
                sparse_table[i - 1][j + (1 << (i - 1))]
            )
 
    return sparse_table
 
def query_sparse_table_plus(sparse_table, left, right):
    k = int(math.log2(right - left + 1))
    return max(
        sparse_table[k][left],
        sparse_table[k][right - (1 << k) + 1]
    )
    
def build_sparse_table_minus(array):
    n = len(array)
    log_n = int(math.log2(n)) + 1
    sparse_table = [[0] * n for _ in range(log_n)]
 
    for i in range(n):
        sparse_table[0][i] = array[i]
 
    for i in range(1, log_n):
        for j in range(n - (1 << i) + 1):
            sparse_table[i][j] = min(
                sparse_table[i - 1][j],
                sparse_table[i - 1][j + (1 << (i - 1))]
            )
 
    return sparse_table
 
def query_sparse_table_minus(sparse_table, left, right):
    k = int(math.log2(right - left + 1))
    return min(
        sparse_table[k][left],
        sparse_table[k][right - (1 << k) + 1]
    )   
    
 
