    x2, y2, x3, y3 = seg()
    x4, y4, x5, y5 = seg()
    x6, y6, x7, y7 = seg()

    def find_corner(x0, y0, x1, y1, x2, y2, x3, y3):
        if x0 == x2 and y0 == y2:
            return x0, y0
        elif x0 == x3 and y0 == y3:
            return x0, y0
        elif x1 == x2 and y1 == y2:
            return x1, y1
        elif x1 == x3 and y1 == y3:
            return x1, y1
        else:
            return None, None

    def flip_seg(cx, cy, x0, y0, x1, y1):
        if cx == x0 and cy == y0:
            return x0, y0, x1, y1
        elif cx == x1 and cy == y1:
            return x1, y1, x0, y0

