def check(n, m, k, v):
    res = 0
    for i in range(1, n + 1):
        res += min(m, v // i)
    return res >= k

def find_kth_largest(n, m, k):
    l = 1
    r = n * m
    while l < r:
        mid = (l + r) // 2
        if check(n, m, k, mid):
            r = mid
        else:
            l = mid + 1
    return r

