def calculateEdges(n: int, m: int) -> int:
    if m == 1:
        return n // 2 if n % 2 == 0 else n // 2 + 1
    if n == 1:
        return m // 2 if m % 2 == 0 else m // 2 + 1
    top, bot, left, right = 0, 0, 0, 0

    top = m // 2 if m % 2 == 0 else m // 2 + 1
    if n % 2 != 0:
        bot = top
    else:
        bot = m // 2
    n -= 2
    m -= 2

    left = n // 2

    if m % 2 != 0:
        right = left
    else:
        right = n // 2 if n % 2 == 0 else n // 2 + 1
    return top + bot + left + right


