def closestToCenter(A,l,r,c,mid):
    if l > r:
        return c
    m = (l + r) // 2
    elem = A[m]
    if abs(elem-mid) < abs(c-mid):
        closest = elem
    else:
        closest = c
    if elem < mid:
        return closestToCenter(A,m+1,r,closest,mid)
    elif elem > mid:
        return closestToCenter(A,l,m-1,closest,mid)
    else:
        return elem

def calcMaxDist(A,l,r,c):
    return max(abs(c-A[l]),abs(c-A[r]))

