from collections import deque

class Node():
    def __init__(self):
        self.data=0
        self.left=None
        self.right=None
        self.count=0
class Trie:
    def __init__(self):
        self.root=Node()
    def add(self,val):
        self.temp=self.root
        for i in range(31,-1,-1):
            v=(val)&(1<<i)
            if v:
                if not self.temp.right:
                    self.temp.right=Node()
                self.temp=self.temp.right
                self.temp.count+=1
            else:
                if not self.temp.left:
                    self.temp.left=Node()
                self.temp=self.temp.left
                self.temp.count+=1
        self.temp.data=val
    def delete(self,val):
        self.temp=self.root
        for i in range(31,-1,-1):
            v=(val)&(1<<i)
            if v:
                self.temp=self.temp.right
                self.temp.count-=1
            else:
                self.temp=self.temp.left
                self.temp.count-=1
    def query(self,val):
        self.temp=self.root
        for i in range(31,-1,-1):
            v=val&(1<<i)
            if v:
                if self.temp.left and self.temp.left.count>0:
                    self.temp=self.temp.left
                elif self.temp.right and self.temp.right.count>0:
                    self.temp=self.temp.right
            else:
                if self.temp.right and self.temp.right.count>0:
                    self.temp=self.temp.right
                elif self.temp.left and self.temp.left.count>0:
                    self.temp=self.temp.left
        return max(val^self.temp.data,val)
    
