def solve_one_seg(d, max_, min_, ans):
    seg = list(d[max_])
    n = len(seg)
    
    if n <= 5:
        for _ in range(max_ - min_):
            ans.append(seg)
    else:
        ori = []
        for i in range(0, n, 2):
            if i == n-1:
                ori[-1].append(seg[i])
            else:
                ori.append([seg[i], seg[i+1]])
        
        for _ in range(max_ - min_):
            ans.extend(ori)    
    
    
def push(d, x, val, i):
    if x not in d:
        d[x] = set()
        
    if val == 1:    
        d[x].add(i)
    else:
        d[x].remove(i)
        
    if len(d[x]) == 0:
        del d[x]
    
def check(d):
    if len(d) != 2:
        return 'none', None
    
    max_ = max(list(d.keys()))
    min_ = min(list(d.keys()))
    
    if len(d[max_]) >= 2:
        return 'seg', [max_, min_]
    
    elif min_ == 0:
        return '0', [max_]
    
    return 'none', None

