    return n, m, grid


def h(t):
    i, n = 0, len(t)
    while i < n and 'B' not in t[i]: i += 1

    while i < n:
        a = t[i].find('B')
        if a < 0:
            i += 1
            break
        b = t[i].rfind('B')
        if 'W' in t[i][a: b + 1]:
            return 1
        i += 1

    while i < n:
        if 'B' in t[i]:
            return 1
        i += 1

    return 0


def sol(n, m, grid):
    t = grid
    p = [''.join(i) for i in zip(*t)]
    if h(p):
        return ["NO"]

    i = 0
    while i < n and 'B' not in t[i]:
        i += 1

    while i < n:
        a = t[i].find('B')
        if a < 0:
            i += 1
            break
        b = t[i].rfind('B')
        if 'W' in t[i][a: b + 1]:
            return ["NO"]

        for j in range(i + 1, n):
