def Prob(w, b, memo={}):
    if w == 0:
        return 0
    elif b == 0:
        return 1
    elif (w, b) in memo:
        return memo[(w, b)]
    else:
        k = w / (b + w)
        if b == 1:
            result = k
        elif b == 2:
            result = k + (1 - k) * ((b - 1) / (w + b - 1)) * Prob(w - 1, b - 2, memo)
        else:
            result = k + (1 - k) * ((b - 1) / (w + b - 1)) * (w / (b + w - 2)) * Prob(w - 1, b - 2, memo) + \
                (1 - k) * ((b - 1) / (w + b - 1)) * ((b - 2) / (b + w - 2)) * Prob(w, b - 3, memo)

        memo[(w, b)] = result
        return result


