    return [[3], [1], [4], [2]]
  if m == 2 and n == 3:
    return [[5, 3], [1, 6], [4, 2]]
  field = [[0 for i in range(m)] for j in range(n)]

  def adv(i, j):
    j += 1
    if j == m:
      j = 0
      i += 1
    return i, j

  def arrange(vals0, vals1):
    i, j = 0, 0
    for val in vals0:
      field[i][j] = val
      i, j = adv(i, j)
    for val in vals1:
      field[i][j] = val
      i, j = adv(i, j)

    good = True
    for i in range(n):
      for j in range(m):
        if i + 1 < n:
          good &= abs(field[i + 1][j] - field[i][j]) > 1
        if j + 1 < m:
          good &= abs(field[i][j + 1] - field[i][j]) > 1
    return good

  all_odd = list(range(1, n * m + 1, 2))
  all_even = list(range(2 , n * m + 1, 2))

  g = arrange(all_odd, all_even)
  if g:
    return field
  all_odd = all_odd[::-1]
  if g:
    return field
  all_even = all_even[::-1]
  if g:
    return field
  all_odd = all_odd[::-1]
  if g:
    return field

  return -1;

