from types import GeneratorType


def bootstrap(f, stack=[]):
	def wrappedfunc(*args, **kwargs):
		if stack:
			return f(*args, **kwargs)
		else:
			to = f(*args, **kwargs)
			while True:
				if type(to) is GeneratorType:
					stack.append(to)
					to = next(to)
				else:
					stack.pop()
					if not stack:
						break
					to = stack[-1].send(to)
			return to

	return wrappedfunc
@bootstrap
def dfs(node,parent,g,dp,wt):
	dp[node]+=wt[node]
	for i in g[node]:
		if i!=parent:
			t=yield dfs(i,node,g,dp,wt)
			dp[node]+=t
	yield dp[node]
