    if x==1:
        return 1,s[0]
    z = sum(s[0:x-1])
    for i in range(x-1,n):
        z+=s[i]
        if (z+k)//x>=s[i]:
            return x,s[i]
        z-=s[i-x+1]







def binary(n,k,s):
    maxi = 0
    l,r = 0,n+1
    while l+1<r:
        mid = (l+r)//2
        if search(n,k,s,mid):
            l = mid
        else:
            r = mid
    return search(n,k,s,l)

