import sys

def backtrack(sol, remaining, b, pos, free):
    if len(sol) == len(b):
        print(sol)
        sys.exit(0)
    for c in '9876543210':
        if not free and c > b[pos]: continue
        if not remaining[ord(c)-ord('0')]: continue
        remaining[ord(c)-ord('0')] -= 1
        backtrack(sol+c, remaining, b, pos+1, free or c < b[pos])
        remaining[ord(c)-ord('0')] += 1

