    if result3 is string {
        test:assertEquals(result3, "First");
    }

    // Test case 4: One dimension too small - table 10x9, radius 5
    // Plate diameter is 10, width is too small (9 < 10)
    string|error result4 = determineWinner(10, 9, 5);
    test:assertTrue(result4 is string);
    if result4 is string {
        test:assertEquals(result4, "Second");
    }

    // Test case 5: Minimum valid case - table 2x2, radius 1
