```ballerina
// Reasoning:
// 1. The pattern of who washes dishes repeats every LCM(M[0], M[1], ..., M[n-1]) days
// 2. We simulate one complete cycle to determine the percentage
// 3. For each day D in the cycle, check if there exists an i such that D mod M[i] <= R[i]
// 4. Calculate percentage as (days_you_wash / total_days_in_cycle)

import ballerina/io;

// Calculate GCD using Euclidean algorithm
function gcd(int a, int b) returns int {
    int absA = a < 0 ? -a : a;
    int absB = b < 0 ? -b : b;

    while absB != 0 {
        int temp = absB;
        absB = absA % absB;
        absA = temp;
    }
    return absA;
}

// Calculate LCM of two numbers
function lcm(int a, int b) returns int {
    if a == 0 || b == 0 {
        return 0;
    }
    int absA = a < 0 ? -a : a;
    int absB = b < 0 ? -b : b;
    return (absA * absB) / gcd(absA, absB);
}

// Calculate LCM of an array of numbers
function lcmArray(int[] arr) returns int {
    if arr.length() == 0 {
        return 1;
    }
    int result = arr[0];
    foreach int i in 1 ..< arr.length() {
        result = lcm(result, arr[i]);
    }
    return result;
}

// Main function to calculate the percentage of days you wash dishes
function calculateWashingPercentage(int[] m, int[] r) returns float {
    // Calculate the period (LCM of all M values)
    int period = lcmArray(m);

    // Count how many days in the period you wash dishes
    int washDays = 0;

    foreach int day in 0 ..< period {
        boolean youWash = false;

        // Check if any condition is satisfied for this day
        foreach int i in 0 ..< m.length() {
            if day % m[i] <= r[i] {
                youWash = true;
                break;
            }
        }

        if youWash {
            washDays += 1;
        }
    }

    // Calculate percentage (as a fraction, not multiplied by 100)
    float percentage = <float>washDays / <float>period;
    return percentage;
}

