// Reasoning:
// The problem involves finding probability that an equation has real roots
// where p ∈ [0, a] and q ∈ [-b, b]
// 
// For a typical anvil trajectory equation: x^2 + 2qx - p = 0
// Discriminant: D = 4q^2 + 4p ≥ 0 for real roots
// This simplifies to: q^2 + p ≥ 0, which is always true for p ≥ 0
// 
// But looking at test cases more carefully:
// For more complex equations like x^2 + 2qx + p = 0
// Discriminant: D = 4q^2 - 4p ≥ 0 => q^2 ≥ p
// 
// Total area: a × 2b
// Valid area where q^2 ≥ p:
// Integrate from q=-b to q=b, for each q, p can be from 0 to min(q^2, a)
//
// If b^2 ≥ a: ∫[-b,b] min(q^2, a) dq
// If b^2 < a: Some parts have full range

import ballerina/io;

function calculateAnvilProbability(int a, int b) returns float {
    // Handle edge case where b = 0
    if b == 0 {
        if a == 0 {
            return 1.0;
        }
        return 0.0;
    }

    // Handle case where a = 0
    if a == 0 {
        return 1.0;
    }

    float aFloat = <float>a;
    float bFloat = <float>b;

    // Total area of the sample space
    float totalArea = aFloat * 2.0 * bFloat;

    // Calculate the area where q^2 >= p (condition for real roots)
    // This is the region where the discriminant is non-negative
    float validArea = 0.0;

    if (bFloat * bFloat >= aFloat) {
        // When b^2 >= a, we integrate from -b to b
        // Valid area = 2 * [integral from 0 to b of min(q^2, a) dq]
        // = 2 * [integral from 0 to sqrt(a) of q^2 dq + integral from sqrt(a) to b of a dq]
        float sqrtA = <float>float:sqrt(aFloat);
        validArea = 2.0 * ((sqrtA * sqrtA * sqrtA / 3.0) + aFloat * (bFloat - sqrtA));
    } else {
        // When b^2 < a
        // Valid area = 2 * integral from 0 to b of q^2 dq
        validArea = 2.0 * (bFloat * bFloat * bFloat / 3.0);
    }

    return validArea / totalArea;
}

public function main() returns error? {
    string? tLine = io:readln();
    if tLine is string {
        int|error t = 'int:fromString(tLine.trim());

        if t is error {
            io:println("Invalid input for test cases");
            return;
        }

        int testCases = t;
        int i = 0;

        while i < testCases {
            string? inputLine = io:readln();
            if inputLine is string {
                string[] parts = inputLine.trim().split(" ");

                if parts.length() == 2 {
                    int|error aVal = 'int:fromString(parts[0]);
                    int|error bVal = 'int:fromString(parts[1]);

                    if aVal is int && bVal is int {
                        float probability = calculateAnvilProbability(aVal, bVal);
                        io:println(string `${float:toString(probability)}`);
                    } else {
                        io:println("Invalid input values");
                    }
                } else {
                    io:println("Invalid input format");
                }
            }
            i = i + 1;
        }
    }
}
