// Reasoning:
// The equation is x^2 + 2qx + p = 0
// For real roots: discriminant D = 4q^2 - 4p >= 0
// This means: q^2 >= p
// 
// Sample space: p in [0, a], q in [-b, b]
// Total area = a * 2b
// 
// Valid region where q^2 >= p:
// For each q, p can range from 0 to min(q^2, a)
// 
// Integral calculation:
// validArea = integral from q=-b to q=b of min(q^2, a) dq
// 
// Case 1: If b^2 >= a (b >= sqrt(a))
//   Split integral at q = sqrt(a):
//   validArea = 2 * [integral(0 to sqrt(a)) q^2 dq + integral(sqrt(a) to b) a dq]
//   = 2 * [(sqrt(a))^3 / 3 + a * (b - sqrt(a))]
//   = 2 * [a^(3/2) / 3 + a*b - a^(3/2)]
//   = 2 * [a*b - 2*a^(3/2)/3]
//   = 2*a*b - 4*a^(3/2)/3
//
// Case 2: If b^2 < a (b < sqrt(a))
//   validArea = 2 * integral(0 to b) q^2 dq
//   = 2 * b^3 / 3

import ballerina/io;

function calculateAnvilProbability(int a, int b) returns float {
    // Handle edge cases
    if b == 0 {
        if a == 0 {
            return 1.0;
        }
        return 0.0;
    }

    if a == 0 {
        return 1.0;
    }

    float aFloat = <float>a;
    float bFloat = <float>b;

    // Total area of sample space
    float totalArea = aFloat * 2.0 * bFloat;

    // Calculate valid area where q^2 >= p
    float validArea = 0.0;

    // Check if b^2 >= a
    float bSquared = bFloat * bFloat;

    if bSquared >= aFloat {
        // b >= sqrt(a) case
        float sqrtA = <float>float:sqrt(aFloat);
        // validArea = 2 * [(sqrtA)^3 / 3 + a * (b - sqrtA)]
        float part1 = (sqrtA * sqrtA * sqrtA) / 3.0;
        float part2 = aFloat * (bFloat - sqrtA);
        validArea = 2.0 * (part1 + part2);
    } else {
        // b < sqrt(a) case
        // validArea = 2 * b^3 / 3
        validArea = 2.0 * (bFloat * bFloat * bFloat) / 3.0;
    }

    return validArea / totalArea;
}

