// 1. This is a longest path problem in a DAG with a time constraint
// 2. We use DP where dp[node][visitCount] = minimum time to reach 'node' visiting exactly 'visitCount' nodes
// 3. Initialize dp with infinity except dp[1][1] = 0 (starting at node 1 with 1 node visited)
// 4. For each state, try all outgoing edges and update the next state
// 5. Find the maximum visitCount where dp[n][visitCount] <= T
// 6. Backtrack to reconstruct the actual path

import ballerina/io;
import ballerina/regex;

const int INF = 2000000000;

type Edge record {
    int to;
    int time;
};

function solveJourney(int n, int m, int T, Edge[][] graph) returns [int, int[]] {
    // dp[node][count] = minimum time to reach node visiting exactly count nodes
    int[][] dp = [];
    int[][] parent = [];

    // Initialize dp table
    foreach int i in 0 ..< n + 1 {
        int[] row = [];
        int[] parentRow = [];
        foreach int j in 0 ..< n + 1 {
            row.push(INF);
            parentRow.push(-1);
        }
        dp.push(row);
        parent.push(parentRow);
    }

    // Base case: at node 1 with 1 node visited, time is 0
    dp[1][1] = 0;

    // DP iteration
    foreach int count in 1 ..< n {
        foreach int u in 1 ... n {
            if dp[u][count] >= INF {
                continue;
            }

            // Try all edges from u
            foreach Edge edge in graph[u] {
                int v = edge.to;
                int time = edge.time;
                int newTime = dp[u][count] + time;

                if newTime < dp[v][count + 1] {
                    dp[v][count + 1] = newTime;
                    parent[v][count + 1] = u;
                }
            }
        }
    }

    // Find maximum count where we can reach node n within time T
    int maxCount = 0;
    foreach int count in 1 ... n {
        if dp[n][count] <= T {
