    test:assertEquals(words2.length(), 5);
    test:assertEquals(words2[0], "1");
    test:assertEquals(words2[1], "");
    test:assertEquals(words2[2], "01");
    test:assertEquals(words2[3], "a0");
    test:assertEquals(words2[4], "");
