    [string, string] result2 = processString("1;;01,a0,");
    test:assertEquals(result2[0], "\"1\"");
    test:assertEquals(result2[1], "\",01,a0,\"");

    // Test case 3: 1
    [string, string] result3 = processString("1");
    test:assertEquals(result3[0], "\"1\"");
    test:assertEquals(result3[1], "-");

    // Test case 4: a
    [string, string] result4 = processString("a");
    test:assertEquals(result4[0], "-");
    test:assertEquals(result4[1], "\"a\"");
}

@test:Config {}
function testEdgeCases() {
    // All numbers
    [string, string] result1 = processString("1,2,3");
    test:assertEquals(result1[0], "\"1,2,3\"");
    test:assertEquals(result1[1], "-");

    // No numbers
    [string, string] result2 = processString("a,b,c");
    test:assertEquals(result2[0], "-");
    test:assertEquals(result2[1], "\"a,b,c\"");

    // Empty words with numbers
    [string, string] result3 = processString(";1;");
    test:assertEquals(result3[0], "\"1\"");
    test:assertEquals(result3[1], "\",\"");

    // Leading zeroes should not be valid
    [string, string] result4 = processString("01,10");
    test:assertEquals(result4[0], "\"10\"");
    test:assertEquals(result4[1], "\"01\"");
}
