import ballerina/test;

@test:Config {}
function testExpectedDressesBasicCases() {
    // Test case 1: k=0 (only last month, just doubling)
    int result = expectedDresses(2, 0);
    test:assertEquals(result, 4, "Failed for x=2, k=0");

    // Test case 2: k=1
    result = expectedDresses(2, 1);
    test:assertEquals(result, 7, "Failed for x=2, k=1");

    // Test case 3: k=2
    result = expectedDresses(3, 2);
    test:assertEquals(result, 21, "Failed for x=3, k=2");

    // Test case 4: x=0 (no initial dresses)
    result = expectedDresses(0, 5);
    test:assertEquals(result, 0, "Failed for x=0, k=5");
}

@test:Config {}
function testExpectedDressesEdgeCases() {
    // Test with x=0, k=0
    int result = expectedDresses(0, 0);
    test:assertEquals(result, 0, "Failed for x=0, k=0");

    // Test with x=1, k=0
    result = expectedDresses(1, 0);
    test:assertEquals(result, 2, "Failed for x=1, k=0");

    // Test with x=1, k=1
    result = expectedDresses(1, 1);
    test:assertEquals(result, 3, "Failed for x=1, k=1");

    // Test with larger k
    result = expectedDresses(1, 3);
    test:assertEquals(result, 15, "Failed for x=1, k=3");
}

@test:Config {}
function testModPow() {
    // Test modular exponentiation
    int result = modPow(2, 10, MOD);
    test:assertEquals(result, 1024, "Failed for 2^10");

    result = modPow(2, 0, MOD);
    test:assertEquals(result, 1, "Failed for 2^0");

    result = modPow(2, 1, MOD);
    test:assertEquals(result, 2, "Failed for 2^1");

    result = modPow(10, 9, MOD);
    test:assertEquals(result, 1000000000, "Failed for 10^9");
}

@test:Config {}
function testLargeNumbers() {
    // Test with large k to ensure modular arithmetic works
    int result = expectedDresses(1000000000, 1000000000);
    test:assertTrue(result >= 0 && result < MOD, "Result should be in valid range");

    // Another large test
    result = expectedDresses(500000000, 500000000);
    test:assertTrue(result >= 0 && result < MOD, "Result should be in valid range");

    // Test large x with small k
    result = expectedDresses(1000000000, 2);
    test:assertTrue(result >= 0 && result < MOD, "Result should be in valid range");

    // Test small x with large k
    result = expectedDresses(5, 1000000000);
    test:assertTrue(result >= 0 && result < MOD, "Result should be in valid range");
}
```
