public function main() returns error? {
    string? input_line = io:readln();

    if input_line is string {
        // Use regex:split instead of string.split()
        string[] parts = regex:split(input_line.trim(), " ");

        if parts.length() != 2 {
            io:println("Error: Please provide exactly two integers");
            return;
        }

        int|error x_val = 'int:fromString(parts[0]);
        int|error k_val = 'int:fromString(parts[1]);

        if x_val is error {
            io:println("Error: First input is not a valid integer");
            return;
        }

        if k_val is error {
            io:println("Error: Second input is not a valid integer");
            return;
        }

        if x_val < 0 || k_val < 0 {
            io:println("Error: Inputs must be non-negative");
            return;
        }

        int result = expectedDresses(x_val, k_val);
        io:println(result);
    } else {
        io:println("Error: Failed to read input");
    }
}
