// Reasoning:
// This is a stars and bars combinatorics problem.
// We have 5 identical "bug fix" pennants and 3 identical "feature" pennants.
// We need to place them on n tables (where n is between 1 and 500).
//
// For k identical items distributed into n distinct bins:
// Number of ways = C(n+k-1, k) = C(n+k-1, n-1)
//
// For 5 bug pennants on n tables: C(n+5-1, 5) = C(n+4, 5)
// For 3 feature pennants on n tables: C(n+3-1, 3) = C(n+2, 3)
//
// These distributions are independent, so total = C(n+4, 5) * C(n+2, 3)
//
// We implement binomial coefficient carefully to avoid overflow:
// C(n,k) = n! / (k! * (n-k)!)
// We calculate iteratively: C(n,k) = (n * (n-1) * ... * (n-k+1)) / (k * (k-1) * ... * 1)

import ballerina/io;

// Calculate binomial coefficient C(n, k)
// Returns n choose k = n! / (k! * (n-k)!)
function binomialCoefficient(int n, int k) returns int|error {
    if k < 0 || n < 0 {
        return error("Invalid input: n and k must be non-negative");
    }

    if k > n {
        return 0;
    }

    if k == 0 || k == n {
        return 1;
    }

    // Optimize: C(n,k) = C(n, n-k), so use smaller value
    int kOpt = k;
    if k > n - k {
        kOpt = n - k;
    }

    // Calculate iteratively to avoid overflow
    // Alternate multiplication and division
    int result = 1;
    int i = 0;
    while i < kOpt {
        result = result * (n - i);
        result = result / (i + 1);
        i = i + 1;
    }

    return result;
}

// Calculate the number of ways to place pennants on n tables
// 5 bug fix pennants and 3 feature pennants
function calculatePennantWays(int n) returns int|error {
    if n < 1 || n > 500 {
        return error("Invalid input: n must be between 1 and 500");
    }

    // Ways to distribute 5 bug pennants among n tables: C(n+4, 5)
    int|error bugWays = binomialCoefficient(n + 4, 5);
    if bugWays is error {
        return bugWays;
    }

    // Ways to distribute 3 feature pennants among n tables: C(n+2, 3)
    int|error featureWays = binomialCoefficient(n + 2, 3);
    if featureWays is error {
        return featureWays;
    }

    // Total ways = product of independent distributions
    return bugWays * featureWays;
}

public function main() returns error? {
    // Read input without prompt (to match expected output format)
    string? input_line = io:readln();

    if input_line is string {
        string trimmed = input_line.trim();
