        result = result / (i + 1);
        i = i + 1;
    }

    return result;
}

// Calculate the number of ways to place pennants on n tables
// 5 bug fix pennants and 3 feature pennants
function calculatePennantWays(int n) returns int|error {
    if n < 1 || n > 500 {
        return error("Invalid input: n must be between 1 and 500");
    }

    // Ways to distribute 5 bug pennants among n tables: C(n+4, 5)
    int|error bugWays = binomialCoefficient(n + 4, 5);
    if bugWays is error {
        return bugWays;
    }

    // Ways to distribute 3 feature pennants among n tables: C(n+2, 3)
    int|error featureWays = binomialCoefficient(n + 2, 3);
    if featureWays is error {
        return featureWays;
    }

    // Total ways = product of independent distributions
    return bugWays * featureWays;
}

public function main() returns error? {
    // Read input without prompt (to match expected output format)
    string? input_line = io:readln();

    if input_line is string {
        string trimmed = input_line.trim();
        int|error n = 'int:fromString(trimmed);

        if n is int {
            int|error result = calculatePennantWays(n);
            if result is int {
